import React, { useState, useEffect } from 'react';
import './Carousel.css';

export default function Carousel() {
  const [slideWidth, setSlideWidth] = useState(0);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const carousel = document.querySelector('.carousel');
    const container = carousel.querySelector('.carousel-container');
    const cards = container.querySelectorAll('.card');

    const handleResize = () => {
      setSlideWidth(carousel.clientWidth);
      slideTo(currentIndex);
    };

    const slideTo = (index) => {
      const numCards = cards.length;
      const adjustedIndex = (numCards + index) % numCards; // Adjusted index for cycling through cards

      // If going back to the first card, slide to the right (negative) instead of left
      if (currentIndex === 0 && index === numCards - 1) {
        container.style.transform = `translateX(${slideWidth}px)`;
        setTimeout(() => {
          container.style.transition = 'none';
          container.style.transform = `translateX(0px)`;
          setTimeout(() => {
            container.style.transition = 'transform 0.5s ease';
          });
        }, 500);
      } else {
        container.style.transform = `translateX(-${adjustedIndex * slideWidth}px)`;
      }

      setCurrentIndex(adjustedIndex);
    };

    const prevBtn = carousel.querySelector('.prev-btn');
    prevBtn.addEventListener('click', () => {
      slideTo(currentIndex - 1);
    });

    const nextBtn = carousel.querySelector('.next-btn');
    nextBtn.addEventListener('click', () => {
      slideTo(currentIndex + 1);
    });

    window.addEventListener('resize', handleResize);
    handleResize(); 

    return () => {
      prevBtn.removeEventListener('click', () => {});
      nextBtn.removeEventListener('click', () => {});
      window.removeEventListener('resize', handleResize);
    };
  }, [currentIndex, slideWidth]);

  return (
    <div className="carousel">
      <div className="carousel-container">
        <div className="card">Card 1 Content</div>
        <div className="card">Card 2 Content</div>
        <div className="card">Card 3 Content</div>
      </div>
      <button className="prev-btn">Prev</button>
      <button className="next-btn">Next</button>
    </div>
  );
}
