import Navbar from '../Components/Navbar'
import Footer from '../Components/Footer'
import './Reviews.css'
import yuki from '../Images/yuki.png'
import emily from '../Images/emily.png'
import michael from '../Images/michael.png'

import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';


export default function Reviews(){
   
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000, // Set autoplay speed in milliseconds (optional)
        adaptiveHeight: true, // Adjust height based on content (optional)
        responsive: [
          {
            breakpoint: 768,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
            },
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
            },
          },
        ],
      };
      
    return(
        <>
        <Navbar/>
        <div className ="firstR">
                    <h1>Rave Reviews</h1>
               
                    <h2>"Stories of Satisfied Customers"</h2>
        </div>
        <div className = "secondR">
        <h1>Reviews</h1>
        <p>What Our Clients Say</p>
             <Slider {...settings} className="secondCar">
      {/* Multiple cards */}
      <div className="card">
        <img src={yuki} className="circular-image" alt="Yuki's photo" />
        <h2>Yuki S.</h2>
        <div className="card-body">
          <p className="card-text">
            “Being a detail-oriented person, I have high standards for myself. Smart Delivery not only meets but exceeds those standards. From the precision in handling delicate fabrics to the seamless delivery, they’ve earned my trust. Smart Delivery is my go-to laundry service.”
          </p>
        </div>
      </div>
      {/* Add more card components similar to the above */}
      {/* Example: */}
      {/* <div className="card"> ... </div> */}

      {/* Carousel controls */}
      {/* This example assumes you're using the default Slick Carousel controls */}
    </Slider>
        </div>
        <div className='seventh'>
            <Footer/>
        </div> 
        </>
    )
}
